// Lifting sate up ====> Update Parent Component State
import React from "react";
import Button from "./Button";

function MyComponent({ state, setState }) {
  function handleClick(e) {
    e.preventDefault();
    setState(!state);
  }

  return (
    <div onClick={handleClick}>
      {state ? (
        <h4>{process.env.REACT_APP_API_URL}</h4>
      ) : (
        <p>Hello My Component</p>
      )}
      <Keyboard />
    </div>
  );
}

export default MyComponent;


function Letters({ isLast, letters }) {
return <div>
  {isLast && (
    <Button>Enter</Button>
  )}
  {letters.split('').map(letter => (
  <Button>{letter}</Button>
  ))}
  {isLast && (
    <Button>Backspace</Button>
  )}
</div>
}


const Keyboard = () => {

  return <div className='keyboard'>
    <Letters isLast={false} letters='qwertyuiop'/>
    <Letters isLast={false} letters='qwertyuiop'/>
    <Letters isLast={false} letters='qwertyuiop'/>
  </div>

}
