import Button from "./Button";


function Letters({ isLast, letters, onKey }) {
  return <div>
    {isLast && (
      <Button onKey={onKey} key='Enter'>Enter</Button>
    )}
    {letters.split('').map(letter => (
      <Button onKey={onKey} key={letter}>{letter}</Button>
    ))}
    {isLast && (
      <Button onKey={onKey} key='Backspace'>Backspace</Button>
    )}
  </div>
}


export const Keyboard = ({ bgColor, onKey }) => {

  return <div className='keyboard'>
    <Letters onKey={onKey} bgColor={bgColor} isLast={false} letters='qwertyuiop'/>
    <Letters onKey={onKey} bgColor={bgColor} isLast={false} letters='asdfghjkl'/>
    <Letters onKey={onKey} bgColor={bgColor} isLast={true} letters='zxcvbnm'/>
  </div>

}
